            // Generate a random color value
            const newPoint = { x: Math.random() * window.innerWidth, y: Math.random() * window.innerHeight}

            // Update the color state
            setPoint(newPoint);
          }, 1000);

          // Clear the interval on unmount
          return () => clearInterval(intervalId);
    }, []);

    return <img src={imageUrl} alt="Moving shark" width={image_width} height={image_height} />;
