import React, { useEffect, useState } from 'react';


function RenderFella({ imageUrl })
{
    const image_width = 128; // Shoud be influenced by actual size of shark
    const image_height = 100;

    useEffect(() => {
        const [goToX, setPointX] = useState( window.innerWidth * 0.5 );

        let intervalCounter = 0;
        let intervalLimit = 0;
        let intervalMaxLimit = 500;

        let goToPoint = { x: 0, y: 0 };

        let flipped = false;

        let wable = 0;
        let wable_amplitud = 0.07;
        let wable_speed = 0.07;

        let vis_w = image_width;
        let vis_h = ( image_height * (1-Math.abs( Math.sin(wable)*wable_amplitud+(1-wable_amplitud) )) ) + ( image_height*(1-wable_amplitud) );
        let vis_x = x - image_width / 2;
        let vis_y = y - (image_width / 2) + image_height - vis_h;

        function updatePosition() {
            intervalCounter++;
            wable += wable_speed;

            if (intervalCounter >= intervalLimit) {
                intervalCounter = 0;
                intervalLimit = intervalMaxLimit * Math.random();
                let new_x = Math.random() * window.innerWidth;
                let new_y = Math.random() * window.innerHeight;
                if (new_x < goToPoint.x) flipped = false;
                else flipped = true;
                goToPoint = { x: new_x, y: new_y };
                setPointX(goToPoint.x);
            }
        }
    });

    return <img src={imageUrl} width={image_width} height={image_height} />;
}

export default RenderFella;
