import React, { useEffect, useState } from 'react';
import "../css/sharkFella.css"


function RenderFella({ imageUrl }) {
    const image_width = 128; // Shoud be influenced by actual size of shark
    const image_height = 100;

    const [point, setPoint] = useState({
        left: Math.random() * (window.innerHeight - image_height),
        top: Math.random() * (window.innerHeight - image_height),
    });
    const [rotation, setRotation] = useState(0);
    const [flip, setFlip] = useState(1);

    let wable = 0;
    let wable_amplitud = 0.07;
    let wable_speed = 0.07;
    /*
    let vis_h = ( image_height * (1-Math.abs( Math.sin(wable)*wable_amplitud+(1-wable_amplitud) )) ) + ( image_height*(1-wable_amplitud) );
    let vis_y = y - (image_width / 2) + image_height - vis_h;
    */

    const setNewPoint = () => {
        const newPoint = {
            left: Math.random() * (window.innerWidth - image_width),
            top: Math.random() * (window.innerHeight - image_height),
        };
        let co = newPoint.top - point.top;
        let ca = newPoint.left - point.left;
        let angle = Math.atan(Math.abs(co) / Math.abs(ca));

        if (ca < 0) setFlip(1);
        else setFlip(-1);

        if ( (co > 0 && ca < 0) || (co < 0 && ca > 0) )
            angle = Math.PI * 2 - angle;
        setRotation(angle);

        setPoint(newPoint);
    }

    useEffect(() => {
        const intervalId = setInterval(setNewPoint, 3000+(Math.random()*2000));
        return () => clearInterval(intervalId);
    }, [point]);

    return <img
        className="sharkFella"
        src={imageUrl}
        alt="Moving shark"
        width={image_width}
        height={image_height}
        style={{ ...point, transform: `rotate(${rotation}rad) scaleX(${flip})` }}
    />;
}

export default RenderFella;
