import React, { useEffect, useState } from 'react';
import "../css/sharkFella.css"


function RenderFella({ imageUrl }) {
    const image_width = 128; // Shoud be influenced by actual size of shark
    const image_height = 100;

    const [point, setPoint] = useState({ top: window.innerWidth * 0.5, left: window.innerHeight * 0.5 });
    const [rotation, setRotation] = useState(0);
    const [flip, setFlip] = useState(1);

    let wable = 0;
    let wable_amplitud = 0.07;
    let wable_speed = 0.07;
    /*
    let vis_w = image_width;
    let vis_h = ( image_height * (1-Math.abs( Math.sin(wable)*wable_amplitud+(1-wable_amplitud) )) ) + ( image_height*(1-wable_amplitud) );
    let vis_x = x - image_width / 2;
    let vis_y = y - (image_width / 2) + image_height - vis_h;
    */

    const setNewPoint = ()=>{
        const newPoint = {
            left: Math.random() * (window.innerWidth - image_width),
            top: Math.random() * (window.innerHeight - image_height),
        };
        let co = newPoint.top - point.top;
        let ca = newPoint.left - point.left;
        if (ca < 0) setFlip(1);
        else setFlip(-1);
        console.log(Math.atan(Math.abs(co) / Math.abs(ca)) * (180 / Math.PI));
        if (co > 0 && ca < 0) {
            console.log("bottom-left");
            setRotation(Math.PI * 2 - Math.atan(Math.abs(co) / Math.abs(ca)));
        } else if (co < 0 && ca < 0) {
            console.log("top-left");
            setRotation(Math.atan(Math.abs(co) / Math.abs(ca)));
        } else if (co < 0 && ca > 0) {
            console.log("top-right");
            setRotation(Math.PI * 2 - Math.atan(Math.abs(co) / Math.abs(ca)));
        } else if (co > 0 && ca > 0) {
            console.log("bottom-right");
            setRotation(Math.atan(Math.abs(co) / Math.abs(ca)));
        }
        console.log("previous top: " + String(point.top));
        console.log("previous left: " + String(point.left));
        console.log("new top: " + String(newPoint.top));
        console.log("new left: " + String(newPoint.left));
        setPoint(newPoint);
    }

    useEffect(() => {
        const intervalId = setInterval(() => {
            setNewPoint()
        }, 4000);
        return () => clearInterval(intervalId);
    }, []);

    return <img
        className="sharkFella"
        src={imageUrl}
        alt="Moving shark"
        width={image_width}
        height={image_height}
        style={{ ...point, transform: `rotate(${rotation}rad) scaleX(${flip})` }}
    />;
}

export default RenderFella;
