import React, { useRef, useEffect, useMemo } from 'react';


function RenderFella({ imageUrl }) {
    console.log("bruh");
    const canvasRef = useRef(null);

    const image_width = 64; // Shoud be influenced by actual size of shark
    const image = useMemo(() => {
        const img = new Image();
        img.src = imageUrl;
        return img;
    }, []);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        let x = canvas.width / 2;
        let y = canvas.height / 2;
        let dx = 2;
        let dy = -2;
        let radius = image_width / 2;

        function drawBall() {
            ctx.drawImage(image, x - image_width / 2, y - image_width / 2, image_width, image_width);
        }

        function updateBallPosition() {
            if (x + dx > canvas.width - radius || x + dx < radius)
                dx = -dx;
            if (y + dy > canvas.height - radius || y + dy < radius)
                dy = -dy;
            x += dx;
            y += dy;
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawBall();
            updateBallPosition();
            requestAnimationFrame(animate);
        }

        animate();
    }, [imageUrl, image]);

    return <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />;
}

class SharkFella extends React.Component {
    constructor(props) {
        super(props);
        this.imageUrl = props.imageUrl;
    }
    render() {
        return 0;
    }
}

export default RenderFella;
