import React, { useRef, useEffect, useMemo } from 'react';


function RenderFella({ imageUrl }) {
    const canvasRef = useRef(null);

    const image_width = 64; // Shoud be influenced by actual size of shark
    const image = useMemo(() => {
        const img = new Image();
        img.src = imageUrl;
        return img;
    }, []);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        let x = canvas.width / 2;
        let y = canvas.height / 2;
        let dx = 0;
        let dy = 0;
        let radius = image_width / 2;

        let intervalCounter = 0;
        let intervalLimit = 100;

        let direction = { x: 1, y: 0 };

        let moving = true;
        let flipped = false;

        function drawBall() {
            if (flipped) {
                ctx.translate(canvas.width, 0);
                ctx.scale(-1, 1);
                ctx.drawImage(image, x - image_width / 2, y - image_width / 2, -image_width, image_width);
                flipped = false;
            } else {
                ctx.drawImage(image, x - image_width / 2, y - image_width / 2, image_width, image_width);
            }
        }

        function updateBallPosition() {
            intervalCounter++;

            if (intervalCounter >= intervalLimit) {
                intervalCounter = 0;
                intervalLimit = 200 * Math.random();
                moving = !moving;
                if (moving) {
                    direction = { x: Math.random() * 2 - 1, y: Math.random() * 2 - 1 };
                    if (dx > 0 && direction.x < 0) flipped = true;
                    dx = direction.x;
                    dy = direction.y;
                }
            }
            if (moving) {
                if (x + dx > canvas.width - radius || x + dx < radius)
                    dx = -dx;
                if (y + dy > canvas.height - radius || y + dy < radius)
                    dy = -dy;
                x += dx;
                y += dy;
            }
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawBall();
            updateBallPosition();
            requestAnimationFrame(animate);
        }

        animate();
    }, [imageUrl, image]);

    return <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />;
}

export default RenderFella;
