import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Banner from "./components";
import Fella from "./extras";
import "./css/commun.css"
import SpeciesCard from './species';


function App() {

  const [backendData, setBackendData] = useState([{}]);

  useEffect(() => {
    fetch("/api").then(
      response => response.json()
    ).then(
      data => {
        setBackendData(data)
      }
    )
  }, [])

  return (
    <Router>
      <Switch>
        <Route path="/shark/:id">
          <SpeciesCard />
        </Route>
        <Route path="/">
          <>
            <Banner />
            <form action="../../post" method="post">
              <button type="submit">Connected?</button>
            </form>
            {(typeof backendData.sharks === 'undefined') ? (
              <p>Loading...</p>
            ) : (
              backendData.sharks.map((shark, i) => (
                <>
                  <div class="entry" key={i}>
                    <div class="basic_info">
                        <a class="name" href="/species">10. Basking Shark (Cetorhinus maximus)</a>
                        <a href="/shark/{shark._id}">{shark.comun_name}</a>
                        <p>- Comun name: {shark.comun_name}.</p>
                        <p>- Scientific name: {shark.science_name}.</p>
                        <p>- Average weight: {shark.weight}kg.</p>
                    </div>
                    <img alt="shark species" src={shark.picture}></img>
                  </div>
                </>
              ))
            )}
            <Fella />
          </>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
