import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Banner from "./modules/components.js";
import Fella from "./modules/extras";
import SpeciesCard from './modules/species.js';
import "./css/commun.css"


function App() {

  const [backendData, setBackendData] = useState([{}]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch("/api");
      console.log(await response.text());
      const jsonData = await response.json();
      console.log("response");
      setBackendData(jsonData);
    }
    fetchData();
  }, [])

  return (
    <>
      <Banner />
      <Router>
        <Routes>
          <Route exact path="/shark/:id" element={<SpeciesCard />} />
          <Route exact path="/" element={
            <>
              <form action="../../post" method="post">
                <button type="submit">Connected?</button>
              </form>
              {(backendData.sharks === undefined) ? (
                <p>Loading...</p>
              ) : (
                backendData.sharks.map((shark, i) => (
                  <>
                    <div class="entry" key={i}>
                      <div class="basic_info">
                        <a class="name" href="/species">10. Basking Shark (Cetorhinus maximus)</a>
                        <a href="/shark/{shark._id}">{shark.comun_name}</a>
                        <p>- Comun name: {shark.comun_name}.</p>
                        <p>- Scientific name: {shark.science_name}.</p>
                        <p>- Average weight: {shark.weight}kg.</p>
                      </div>
                      <img alt="shark species" src={shark.picture} />
                    </div>
                  </>
                ))
              )}
            </>
          } />
          <Route exact path="*" element={<p>Error: page not found</p>} />
        </Routes>
      </Router>
      <Fella />
    </>
  );
}

export default App;
