import React, { useRef, useEffect, useMemo } from 'react';


function RenderFella({ imageUrl }) {
    const canvasRef = useRef(null);

    const image_width = 64; // Shoud be influenced by actual size of shark
    const image_height = 64;
    const image = useMemo(() => {
        const img = new Image();
        img.src = imageUrl;
        return img;
    }, []);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        let x = canvas.width / 2;
        let y = canvas.height / 2;
        let dx = 0;
        let dy = 0;
        let radius = image_height / 2;

        let intervalCounter = 0;
        let intervalLimit = 100;

        let direction = { x: 1, y: 0 };

        let moving = true;
        let flipped = false;

        let wable = 0;
        let wable_amplitud = 0.07;

        function drawFella() {
            let vis_w = image_width;
            let vis_h = ( image_height * (1-Math.abs( Math.sin(wable)*wable_amplitud+(1-wable_amplitud) )) ) + ( image_height*(1-wable_amplitud) );
            let vis_x = x - image_width / 2;
            let vis_y = y - (image_width / 2) + image_height - vis_h;
            ctx.drawImage(image, vis_x, vis_y, vis_w, vis_h);
        }

        function updateBallPosition() {
            intervalCounter++;
            wable += 0.07;
            if (intervalCounter >= intervalLimit) {
                intervalCounter = 0;
                intervalLimit = 50 * Math.random();
                moving = !moving;
                direction = { x: Math.random() * 2 - 1, y: Math.random() * 2 - 1 };
                dx = direction.x;
                dy = direction.y;
                if (dx < 0) flipped = true;
                else flipped = false;
            }
            if (moving) {
                if (x + dx > canvas.width - radius || x + dx < radius)
                    dx = -dx;
                if (y + dy > canvas.height - radius || y + dy < radius)
                    dy = -dy;
                x += dx;
                y += dy;
            }
        }

        function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawFella();
            updateBallPosition();
            requestAnimationFrame(animate);
        }

        animate();
    }, [imageUrl, image]);

    return <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />;
}

export default RenderFella;
