import React, { useEffect, useState } from 'react';
import "../css/sharkFella.css"


function RenderFella({ imageUrl }) {
    const image_width = 128; // Shoud be influenced by actual size of shark
    const image_height = 100;

    const [point, setPoint] = useState({ top: window.innerWidth * 0.5, left: window.innerHeight * 0.5 });
    const [rotation, setRotation] = useState(0);

    let flipped = false;

    let wable = 0;
    let wable_amplitud = 0.07;
    let wable_speed = 0.07;
    /*
    let vis_w = image_width;
    let vis_h = ( image_height * (1-Math.abs( Math.sin(wable)*wable_amplitud+(1-wable_amplitud) )) ) + ( image_height*(1-wable_amplitud) );
    let vis_x = x - image_width / 2;
    let vis_y = y - (image_width / 2) + image_height - vis_h;
    */
    useEffect(() => {
        const intervalId = setInterval(() => {
            const newPoint = {
                left: Math.random() * (window.innerWidth - image_width),
                top: Math.random() * (window.innerHeight - image_height),
            };
            setRotation(Math.atan(Math.abs(newPoint.top - point.top) / Math.abs(newPoint.left - point.left)) * (180 / Math.PI));
            console.log(Math.atan(Math.abs(newPoint.top - point.top) / Math.abs(newPoint.left - point.left)) * (180 / Math.PI));
            setPoint(newPoint);
        }, 2000);
        return () => clearInterval(intervalId);
    }, []);

    return <img 
        className="sharkFella"
        src={imageUrl}
        alt="Moving shark"
        width={image_width}
        height={image_height}
        style={{ ...point, transform: `rotate(${rotation}deg)` }}
    />;
}

export default RenderFella;
