function Ball() {
    const canvasRef = useRef(null);

    useEffect(() => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      let x = canvas.width / 2;
      let y = canvas.height / 2;
      let dx = 0;
      let dy = 0;
      let radius = 10;

      let intervalCounter = 0;
      let intervalLimit = 60; // Change direction every 60 frames

      let direction = { x: 1, y: 0 }; // Start by going right
      let amplitude = 20; // Oscillation amplitude
      let period = 60; // Oscillation period (in frames)
      let angle = 0; // Oscillation angle

      function drawBall() {
        ctx.beginPath();
        ctx.arc(x, y, radius, 0, Math.PI * 2);
        ctx.fillStyle = '#0095DD';
        ctx.fill();
        ctx.closePath();
      }

      function updateBallPosition() {
        intervalCounter++;

        if (intervalCounter >= intervalLimit) {
          // Change direction and reset interval counter
          intervalCounter = 0;
          direction = { x: Math.random() * 2 - 1, y: Math.random() * 2 - 1 };
        }

        // Update position based on direction
        dx = direction.x * 2;
        dy = direction.y * 2;
        x += dx;
        y += dy;

        // Oscillate height
        angle += (2 * Math.PI) / period;
        y += amplitude * Math.sin(angle);
      }

      function animate() {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        drawBall();
        updateBallPosition();
        requestAnimationFrame(animate);
      }

      animate();
    }, []);

    return <canvas ref={canvasRef} width={window.innerWidth} height={window.innerHeight} />;
  }

  export default Ball;
