import React, { Component } from 'react';


class TimeComponent extends Component {
    constructor(props){
        super(props);
        this.state = { time: Date.now() };
        this.imageUrl = props.imageUrl;
        this.image_width = 128; // Shoud be influenced by actual size of shark
        this.image_height = 100;
        this.key = props.key;
        this.pos = {
            left: 100,
            top: 200,
        };
    }
    render(){
        return (
            <>
                <div> { this.state.time } </div>
                <img
                    class={`sharkFella_${this.key}`}
                    alt="moving_shark" src={this.imageUrl}
                    width={this.image_width}
                    height={this.image_height}
                    style={{
                            position: 'absolute',
                            top: this.pos.top,
                            left: this.pos.left,
                        }}
                />
            </>
        );
    }
    componentDidMount() {
        this.interval = setInterval(() => this.setState({ time: Date.now() }), 30);
        this.interval = setInterval(() => this.setState({ pos_top: this.pos.top+1 }), 30);
        this.pos.top = this.state.pos_top;
    }
    componentWillUnmount() {
        clearInterval(this.interval);
    }
}

export default TimeComponent;
